/* @baozhou */
@charset "utf-8";
body,div,dl,dt,dd,ul,ol,li,h1,h2,h3,h4,h5,h6,pre,form,fieldset,input,textarea,p,blockquote,th,td {margin:0;padding:0;}
table {border-collapse:collapse;border-spacing:0;}
fieldset,img {border:0;}
address,caption,cite,code,dfn,em,strong,th,var {font-style:normal;font-weight:normal;}
ol,ul {list-style:none;}
h1,h2,h3,h4,h5,h6 {font-size:100%;font-weight:normal;}
b{font-weight:normal;}
button {cursor:pointer;}
input, select, form img, button,label,textarea{font-size:12px;vertical-align:middle;font-family:Tahoma;color:#daac79;}
a{text-decoration:none;}
a:hover{text-decoration:none}
img{display:block;}
.clearfix:after, .anlist:after{content:""; display:block; clear:both;}
.clearfix, .anlist{zoom:1;}
.clear{height:0;overflow:hidden;clear:both;}

/* CSS Document */
body{background-color:#101115;}
#listing_tpl_body{width:950px; margin:auto; background-color:#101115;}
#listing_tpl_header_log{ text-align:center;}
#listing_tpl_header_log img{margin:auto}
#listing_tpl_header_nav ul li{ float:left; display:block; padding:13px 15px 7px 15px; color:#fff; font-size:16px; background:url(./images/listing_tpl_watch01_05.jpg) no-repeat right top;}
#listing_tpl_header_nav ul li a{color:#D3D3D3;}
#listing_tpl_header_nav ul li a:hover{color:#fff; text-decoration:none;}

#listing_tpl_header_search{float:right; margin:10px 10px 0px 0px; height:25px;}
#listing_tpl_header_search span{width:30px; height:30px; line-height:30px;}
#home_tpl_header_search_input{ height:28px;}

#listing_tpl_center_show{width:940px; margin:auto}
#listing_tpl_center_list{padding-left:7px;}
#listing_tpl_center_list ul{display:block;}
/*#listing_tpl_center_list ul li{border:1px solid #000; float:left; display:block;}*/
#listing_tpl_center_list ul li{float:left; display:block;}
#listing_tpl_center_list img{width:232px; height:131px;}

#listing_tpl_preview_content{width:945px; margin:auto;}
.small_pic ul li{border:2px solid #000;margin-bottom:3px;}
.big_pic{margin-left:10px;}

#listing_tpl_description_content{font-size:16px; color:#fff; padding:10px;}

#listing_tpl_hot_sale_content{padding-left:9px;}
#listing_tpl_hot_sale_content ul li{display:block; float:left; padding:2px;}
#listing_tpl_hot_sale_content img{border-radius:10px; border:1px solid #fff}
#listing_tpl_brand h2 img{ margin:auto;}

#listing_tpl_rule #rule_tab{ background:url(./images/listing_tpl_watch01_06.jpg); border:1px #666 solid; padding-left:30px;}
#listing_tpl_rule #rule_tab li a{ background:none; color:#ccc; font-size:16px; border:none;}
#listing_tpl_rule #rule_tab li.active a{color:#fff; font-weight:bold;}

#listing_tpl_rule_content .tab-content{padding:20px 0px; margin:auto; text-align:center; width:900px;}


/* 焦点BANNER */
.bnspic a{display:block;}
.big_pic, .small_pic{float:left;}
.big_pic, .big_pic a, .big_pic img{width:700px;height:700px;}
.big_pic{overflow:hidden;position:relative;}
.big_pic li{position:absolute;top:0;left:0;opacity:0;-webkit-transform:scale(0.9);transform:scale(0.4);z-index:-1;transition:all linear 0.2s;}
.big_pic .on{opacity:1;-webkit-transform:scale(1);transform:scale(1);z-index:1;}
.big_pic a{position:relative;}
.big_pic .txt{width:681px;height:46px;line-height:46px;display:block;position:absolute;bottom:0;left:0;color:#BDBDBD;text-align:center;filter:progid:DXImageTransform.Microsoft.gradient(enabled='true',startColorstr='#7F000000', endColorstr='#7F000000');background:rgba(0,0,0,0.5);transition:all ease .2s;white-space:nowrap;overflow:hidden;text-overflow:ellipsis;}
.big_pic a:hover .txt{color:#FFFFFF;}
.small_pic{width:230px;position:relative;left:1px;}
.small_pic a, .small_pic img{width:110px;height:110px; cursor:pointer;}
.small_pic a{transition:all ease .2s;}
.small_pic .on, .small_pic li:hover{position:relative;z-index:1;}
.small_pic .on a{}
.small_pic li{margin:-1px 0 0 -1px;float:left;}

/* Common Class */
.block_float_left{float:left;}
.block_float_right{float:right;}
.clearboth{ clear:both;}
.back{ background-color:black;}
.barflag{ background-image:url(./images/listing_tpl_watch01_06.jpg); height:43px;}
.barflag_left{ background-image:url(./images/listing_tpl_watch01_14.jpg); display:block; width:5px; height:43px; float:left;}
h2 .barflag_title{color:#fff; padding:5px 0px 0px 30px; float:left; font-size:28px;}

#listing_tpl_hot_sale_content img{width:228px; height:229px;}
.opaimg img{opacity:0.8;filter(alpha=80)}
.opaimg img:hover {opacity:1;filter(alpha=100)}

#img_main{display:block; width:700px; height:700px; margin-left:10px;}
#img_thumb {display:block; width:230px;}
#img_thumb a{float:left; margin-left:5px; margin-bottom:5px;}

/**base style*/
#xf_ebt_listingtplheader{height:150px}
#xf_ebt_listingtplcenter{width:950px;height:820px}
#xf_ebt_listingtplcontent{height:520px}
#xf_ebt_listingtplcontentbottom{height:3000px}
